import React from 'react'
import Card from './Card'
import data from "./data"
import { useNavigate } from "react-router-dom";
import './cards.css'
import { filter } from '@chakra-ui/react';


function PublicForm() {

  const navigate = useNavigate();

  let averagePrice = 0
  for (let i = 0; i < data.length; i++){
    averagePrice+= data[i].price
  }
  averagePrice = averagePrice / data.length
  let data1 = []
  let data2 = []
  let counter = 0
  for(let i = 0; i < data.length; i++) {
    data1.push(data[counter])
    counter+=1
  }


    const cardsLeft = data1.map(item => {
      return (
        <Card 
        key = {item.id}
        {...item}
        />
      ) 
    })

    const [cards, setCards] = React.useState(cardsLeft);

    function filterItems(change) {
      setCards(prevCards => {
        filter(prevCards, change)
      })
    }

    const navToHome = () => {
      navigate("/");
  }

  return <>
  <button onClick={navToHome}>Return Home</button>
  <form className='nav'>
    <label className='center'>Search Bar</label>
    <input for="service" type="text" onChange={filterItems}></input>
  </form>
  <div className='public-page'>
      <section className='cards-list'>
        {cardsLeft}
      </section>
      {/* <section className='cards-Right'>
        {cardsRight}
      </section> */}
    </div>
    <h1 className='avg'>Average Cost of Serivce: {averagePrice}</h1>
  </>

}

export default PublicForm;
